import { Component, Input, OnInit } from '@angular/core';

@Component({
  selector: 'app-to-do-list',
  templateUrl: './to-do-list.component.html',
  styleUrls: ['./to-do-list.component.scss'],
})
export class ToDoListComponent implements OnInit {
  @Input()
  list: any[] = [];

  itemSelected: any = {};
  constructor() {}

  ngOnInit(): void {}
  /*   delete(item: any): void {
    if(!item.name)
    return;
    debugger;
    let index = this.list.findIndex(x => x.name == item.name);
    //delete this.list[index];
    this.list.splice(index,1);
  } */
}
<div class="to-do-list">
  Cantidad: {{ list.length }}
  <ng-container *ngFor="let item of list">
    <!--     <div class="task-item" *ngIf="item" [ngClass]="{'task-item-default':item.default==true}">
      <span>{{ item.name }}</span>
      <a (click)="delete(item)">Eliminar <i class="fa-solid fa-trash-can"></i></a>
    </div> -->

    <app-to-do-item [item]="item" [list]="list"></app-to-do-item>
  </ng-container>

  <div class="mb-3">
    <label for="" class="form-label">Task</label>
    <select
      class="form-select form-select-lg"
      [(ngModel)]="itemSelected"
      name="slcTask"
    >
      <option *ngFor="let item of list">{{ item.name }}</option>
    </select>
    {{ itemSelected | json }}
  </div>
</div>
