import { Component, Input, OnInit } from '@angular/core';
import { ToastrService } from 'ngx-toastr';


@Component({
  selector: 'app-to-do-add',
  templateUrl: './to-do-add.component.html',
  styleUrls: ['./to-do-add.component.scss'],
})
export class ToDoAddComponent implements OnInit {

  @Input()
  list: any[] = [];

  newTask: any = {};

  ngOnInit(): void {}
  constructor(private toastrService: ToastrService) {}

  add(): void {
    if (!this.newTask.name) return;
    this.list.push(this.newTask);
    this.newTask = {};
    this.toastrService.success('ToDo App','Nueva tarea agregada')
  }
}
<div class="task-add">

  <input
    type="task"
    class="form-control"
    [(ngModel)]="newTask.name"
    placeholder="Ingresar tarea"
  />
  <input class="btn btn-primary" (click)="add()" type="button" value="Agregar"  >
</div>
