@Component({
  selector: 'app-to-do',
  templateUrl: './to-do.component.html',
  styleUrls: ['./to-do.component.scss']
})
export class ToDoComponent implements OnInit {
  title = 'todo-list-app';
  list: any[] = [];
  newTask: any={};

  ngOnInit(): void {


  }
  add(): void {
    if(!this.newTask.name)
    return;
    this.list.push(this.newTask);
    this.newTask={};
  }


}
